import React, { Component } from 'react';
import plant from '../graphics/icon.png';

class NetworkTab extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addFriend: false
    };
  }

  setAddFriend = (bool) => {
    this.setState({
      addFriend: bool
    });
  }

  render()
  {
    let modal = null;
    if (this.state.addFriend){
      modal = (
        <div className="modal network">
          <button className="close">
            <span className="jam jam-close"></span>
          </button>
          <img src={plant}></img>
          <div className="add-friend">
            <h2>add friend</h2>
            <div className="network-input">
              <input type="email" placeholder="what's their email?"></input>
              <button>send request</button>
            </div>
          </div>
          <div className="invite-friend">
            <h2>invite friend</h2>
            <div className="network-input">
              <input type="email" placeholder="what's their email?"></input>
              <button>send invite</button>
            </div>
          </div>
        </div>
      );
    }

    return (
      <section className="network">
        <div className="header">
          <div className="search-bar">
            <input type="text" placeholder="search"/>
            <button id="search-friends"><span className="jam jam-search" style={{color: '#9FC6C1'}}></span></button>
          </div>
          <button id="add-friends" onClick={()=>this.setAddFriend(true)}><span className="jam jam-user-plus" style={{color: '#9FC6C1'}}></span></button>
        </div>
        <div className="friends">
          <div className="friend">
            <div className="pic"></div><span>Felicia Wilson</span>
            <button><span className="jam jam-heart" style={{color: '#e38882'}}></span></button>
          </div>
        </div>
        {modal}
      </section>
    );
  }
};

export default NetworkTab;
