import React, { Component } from 'react';

const Conversation = (props) => {
  let classN;
  if (props.poster) {
    classN = 'reply';
  } else {
    classN = 'comment';
  }

  return (
    <div className={classN}>
      <Avatar PpfURL={props.PpfURL} />
      <span>{props.message}</span>
    </div>
  );
};

export default Conversation;
