import React, { Component } from 'react';
import $ from 'jquery';
import plants from './../graphics/plants.png';
import hero from './../graphics/hero.gif';
import { FirebaseContext } from './Firebase';

import SignupForm from './SignupForm';
import LoginForm from './LoginForm';

class HomePage extends React.Component { 
  constructor(props) {
    super(props);
    this.state = {
      signingUp: false,
      loggingIn: false
    };
  } 

  setSignUp = (bool) => {
    this.setState({
      signingUp: bool
    });
  }

  setLogin = (bool) => {
    this.setState({
      loggingIn: bool
    });
  }

  render (){ 
    var content;
    if (this.state.loggingIn){
      content = (
        <FirebaseContext.Consumer>
        {firebase => 
        <LoginForm 
          checkLogin = {this.props.checkLogin}
          setLogin = {this.setLogin}
          firebase={firebase} />}
      </FirebaseContext.Consumer>
      );
    }else if (this.state.signingUp){
      content = (
        <FirebaseContext.Consumer>
        {firebase => 
        <SignupForm 
          SignUp = {this.props.SignUp}
          setSignUp = {this.setSignUp}
          firebase={firebase} />}
      </FirebaseContext.Consumer>
      );
    }else{
      content = (
        <div>
          <h2>my_friends</h2>
          <p>give and get support from your support network. feel safe with the ones you trust.</p>
          <div className="buts">
            <button className="signup-but" onClick={()=>this.setSignUp(true)} >sign up</button>
            <button className="login-but" onClick={()=>this.setLogin(true) }>login</button>
          </div>
        </div>
      );
    }

    return (
      <div className="home-page">   
        <img className="bg-texture" src={hero}/>
        <img src={plants}/>
      {content}
    </div>
    );
  }
}

export default HomePage;
